import React, { useState } from 'react';

function Form_GaussSeidel() {
    const [matrix, setMatrix] = useState([[0, 0], [0, 0]]);
    const [b, setB] = useState([0, 0]);

    const handleMatrixChange = (row, col, value) => {
        const newMatrix = [...matrix];
        newMatrix[row][col] = parseFloat(value);
        setMatrix(newMatrix);
    };

    const calculateGaussSeidel = () => {
        // ใส่ logic การคำนวณ Gauss-Seidel Iteration ที่นี่
        console.log("Calculating Gauss-Seidel for matrix: ", matrix);
    };

    return (
        <div>
            <h2>Gauss-Seidel Iteration</h2>
            <div>
                {matrix.map((row, rowIndex) => (
                    <div key={rowIndex}>
                        {row.map((value, colIndex) => (
                            <input
                                key={colIndex}
                                type="number"
                                value={value}
                                onChange={(e) => handleMatrixChange(rowIndex, colIndex, e.target.value)}
                            />
                        ))}
                    </div>
                ))}
            </div>
            <button onClick={calculateGaussSeidel}>Calculate Gauss-Seidel</button>
        </div>
    );
}

export default Form_GaussSeidel;
